#list of  pies displayed pie_list = ["Pecan", "Apple Crisp", "Bean", "Banoffee", "Black Bun",            "Blueberry", "Buko", "Burek", "Tamale", "Steak"]# list used to store all of the pies when selected pie_cart = []print("Welcome to the House of Pies! Here are our pies:")buying_pies = "y"for pie in pie_list:    print(f'{pie_list.index(pie)} - {pie}')    while buying_pies == "y":    print("Which candy would you like to bring home?")    pies_selected = int(input("Input the number of the pie you want: "))    pie_cart.append(pie_list[pies_selected])    for pie in pie_cart:        print("Great! We'll have that " + pie +  " right out for you.")        buying_pies = input(("Would you like to purchase another: (y)es or (n)o? "))print("Great! These are your purchases:")for pie in pie_cart:    print(pie)        